import React, { useEffect, useState } from 'react';
import WalletConnector from './components/WalletConnector';

const App = () => {
  const [balance, setBalance] = useState(0);
  const [currentBet, setCurrentBet] = useState(1);
  const [bets, setBets] = useState([]);

  // –§—É–Ω–∫—Ü—ñ—è –¥–ª—è –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –±–∞–ª–∞–Ω—Å—É
  const fetchBalance = async () => {
    // –û—Ç—Ä–∏–º–∞—Ç–∏ –±–∞–ª–∞–Ω—Å –∑ –≥–∞–º–∞–Ω—Ü—è
    // –ü—Ä–∏–∫–ª–∞–¥:
    // setBalance(await getWalletBalance());
  };

  // –§—É–Ω–∫—Ü—ñ—è –¥–ª—è –æ–±—Ä–æ–±–∫–∏ –Ω–æ–≤–æ—ó —Å—Ç–∞–≤–∫–∏
  const handleNewBet = async () => {
    const newBet = {
      address: '0x000c3877DE5ae7B74b2dd8afD54B306D9c43fD80', // –ó–∞–º—ñ–Ω–∏ –Ω–∞ –∞–¥—Ä–µ—Å—É –∫–æ—Ä–∏—Å—Ç—É–≤–∞—á–∞
      amount: currentBet,
      time: new Date().toLocaleTimeString(),
    };

    // –û–Ω–æ–≤–ª—é—î–º–æ —Å–ø–∏—Å–æ–∫ —Å—Ç–∞–≤–æ–∫, –¥–æ–¥–∞—é—á–∏ –Ω–æ–≤—É –Ω–∞ –ø–æ—á–∞—Ç–æ–∫
    setBets(prevBets => [newBet, ...prevBets].slice(0, 10));
    
    // –ó–±—ñ–ª—å—à—É—î–º–æ —Å—É–º—É –Ω–∞—Å—Ç—É–ø–Ω–æ—ó —Å—Ç–∞–≤–∫–∏ –Ω–∞ 1 Quai
    setCurrentBet(currentBet + 1);
  };

  useEffect(() => {
    fetchBalance();
  }, []);

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '10px' }}>
        <span>–ë–∞–ª–∞–Ω—Å: {balance} Quai</span>
        <WalletConnector />
      </div>
      <h1>Game DApp</h1>
      <button onClick={handleNewBet} style={{ padding: '10px 20px', fontSize: '16px' }}>
        –ù–∞–¥—ñ—Å–ª–∞—Ç–∏ —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—é ({currentBet} Quai)
      </button>
      
      <h3>–°–ø–∏—Å–æ–∫ —Å—Ç–∞–≤–æ–∫:</h3>
      <table border="1" style={{ width: '100%', textAlign: 'center', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>–ì–∞–º–∞–Ω–µ—Ü—å</th>
            <th>–°—Ç–∞–≤–∫–∞</th>
            <th>–ß–∞—Å —Å—Ç–∞–≤–∫–∏</th>
          </tr>
        </thead>
        <tbody>
          {bets.map((bet, index) => (
            <tr key={index} style={index === 0 ? { fontWeight: 'bold', backgroundColor: '#FFD700' } : {}}>
              <td>{bet.address.slice(0, 6)}...{bet.address.slice(-4)}</td>
              <td>{bet.amount} Quai {index === 0 && 'üëë'}</td>
              <td>{bet.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
