import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [wallet, setWallet] = useState(null); // Триматимемо адресу гаманця
  const [balance, setBalance] = useState(null); // Триматимемо баланс
  const [betAmount, setBetAmount] = useState(1); // Ставка гравця
  const [bets, setBets] = useState([]); // Список ставок

  // Підключення до гаманця та отримання балансу
  const requestAccounts = async () => {
    if (window.pelagus && window.pelagus.request) {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const balance = await getBalance(accounts[0]);
        setWallet(accounts[0]); // Зберігаємо адресу гаманця
        setBalance(balance); // Оновлюємо баланс
        document.getElementById('connect-wallet').textContent = `${shortenAddress(accounts[0])}`; // Відображаємо скорочену адресу гаманця
      } catch (error) {
        console.error('Error connecting to Pelagus Wallet:', error);
      }
    } else {
      alert('Pelagus Wallet не знайдено');
    }
  };

  // Слухаємо подію зміни акаунта
  useEffect(() => {
    if (window.pelagus) {
      window.pelagus.on('accountsChanged', (accounts) => {
        if (accounts && accounts[0] !== wallet) {
          setWallet(accounts[0]); // Оновлюємо адресу гаманця при зміні акаунта
          getBalance(accounts[0]).then(balance => setBalance(balance)); // Оновлюємо баланс
        }
      });
    }
  }, [wallet]); // Залежність на wallet

  // Функція для отримання балансу
  const getBalance = async (address) => {
    const options = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        jsonrpc: "2.0",
        method: "quai_getBalance",
        params: [address, "latest"],
        id: 1
      })
    };

    try {
      const response = await fetch('https://rpc.quai.network/cyprus1/', options);
      const result = await response.json();

      if (result && result.result) {
        // Перетворення балансу з Wei в Quai
        return parseFloat(result.result) / 1e18;
      } else {
        console.error("Не вдалося отримати баланс");
        return 0; // Якщо немає результату
      }
    } catch (err) {
      console.error("Помилка при отриманні балансу:", err);
      return 0; // Повертає 0 у разі помилки
    }
  };

  // Обробник події для підключення до гаманця
  document.getElementById('connect-wallet').addEventListener('click', async () => {
    if (typeof window.pelagus !== 'undefined') {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const account = accounts[0];

        // Отримання балансу
        const balance = await getBalance(account);
        
        // Відображення балансу на кнопці
        document.getElementById('connect-wallet').textContent = `${balance.toFixed(4)} Quai`; // Відображення з 4 знаками після коми

        // Оновлення адреси (якщо потрібно)
        document.getElementById('wallet-address').textContent = `${shortenAddress(account)}`;
      } catch (error) {
        console.error("Помилка підключення до Pelagus Wallet:", error);
      }
    } else {
      alert("Pelagus Wallet не виявлено.");
    }
  });

  // Допоміжна функція для скорочення адреси
  const shortenAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  // Функція для відправки транзакції
  const sendTransaction = async () => {
    const recipientAddress = '0x000c3877DE5ae7B74b2dd8afD54B306D9c43fD80';
    const amountToSend = betAmount; // Ставка гравця
    const weiAmount = (parseFloat(amountToSend) * 1e18).toString(16); // Конвертуємо в wei та перетворюємо в шістнадцятковий формат

    if (window.pelagus && window.pelagus.request) {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const transactionParameters = {
          from: accounts[0],            // Адреса користувача
          to: recipientAddress,         // Кінцева адреса
          value: '0x' + weiAmount,      // Кількість токенів у wei
          gasLimit: '0x5208',           // Ліміт газу (21000 в hex)
          gasPrice: '0x3b9aca00',       // Ціна газу в wei (1000000000)
        };

        const txHash = await window.pelagus.request({
          method: 'quai_sendTransaction',
          params: [transactionParameters],
        });

        console.log('Транзакція надіслана:', txHash);
        alert('Транзакція успішно надіслана. Хеш транзакції: ' + txHash);
        addBet(); // Додаємо ставку в таблицю лише після успішної транзакції
      } catch (error) {
        console.error('Помилка надсилання транзакції:', error);
        alert('Транзакція не вдалася. Перевірте консоль для деталей.');
      }
    } else {
      alert('Гаманець Pelagus не знайдено.');
    }
  };

  // Додавання ставки до таблиці
  const addBet = () => {
    const newBet = {
      wallet: wallet,
      amount: `${betAmount} Quai`,
      time: new Date().toLocaleTimeString(),
    };
    setBets([newBet, ...bets]); // Додаємо нову ставку на початок таблиці
    setBetAmount(prevBetAmount => prevBetAmount + 1); // Збільшуємо ставку на 1 Quai
  };

  return (
    <div className="App">
      <h1>Game DApp</h1>
      <div className="wallet-info">
        {/* Кнопка для відображення балансу */}
        <button className="wallet-balance-btn" disabled>
          {balance !== null ? `Баланс: ${balance} Quai` : 'Завантаження балансу...'}
        </button>
        {/* Кнопка для підключення гаманця */}
        <button className="connect-wallet-btn" onClick={requestAccounts}>
          {wallet ? shortenAddress(wallet) : 'Connect Wallet'}
        </button>
      </div>
      <div>
        <h3>Ставка: {betAmount} Quai</h3>
        <button onClick={sendTransaction}>Cowboy</button>
      </div>
      <h3>Список ставок:</h3>
      <table>
        <thead>
          <tr>
            <th>Гаманць</th>
            <th>Ставка</th>
            <th>Час ставки</th>
          </tr>
        </thead>
        <tbody>
          {bets.map((bet, index) => (
            <tr key={index} className={index === 0 ? "winner" : ""}>
              <td>
                {/* Додаємо іконку корони для останньої ставки */}
                {index === 0 && <span className="crown-icon">👑</span>}
                {bet.wallet}
              </td>
              <td>{bet.amount}</td>
              <td>{bet.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
