import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [wallet, setWallet] = useState(null); // Зберігаємо адресу гаманця
  const [balance, setBalance] = useState(null); // Зберігаємо баланс гаманця

  // Функція для отримання балансу
  const getBalance = async (address) => {
    const options = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        jsonrpc: "2.0",
        method: "quai_getBalance",
        params: [address, "latest"],
        id: 1
      })
    };

    try {
      const response = await fetch('https://rpc.quai.network/cyprus1/', options);
      const result = await response.json();
      if (result && result.result) {
        return parseFloat(result.result) / Math.pow(10, 18); // Перетворення балансу з Wei в Quai
      }
      return 0;
    } catch (err) {
      console.error(err);
      return 0; // Повертає 0 у разі помилки
    }
  };

  // Функція для підключення гаманця та отримання балансу
  const requestAccounts = async () => {
    if (window.pelagus && window.pelagus.request) {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const balance = await getBalance(accounts[0]); // Отримуємо баланс
        setWallet(accounts[0]); // Оновлюємо адресу гаманця
        setBalance(balance); // Оновлюємо баланс
      } catch (error) {
        console.error('Помилка при підключенні до гаманця Pelagus:', error);
      }
    } else {
      alert('Гаманець Pelagus не знайдено');
    }
  };

  return (
    <div className="App">
      <h1>Game DApp</h1>
      <div className="wallet-info">
        {/* Кнопка для відображення балансу */}
        <button className="wallet-balance-btn" disabled>
          {balance !== null ? `Баланс: ${balance} Quai` : 'Завантаження балансу...'}
        </button>
        {/* Кнопка для підключення гаманця */}
        <button className="connect-wallet-btn" onClick={requestAccounts}>
          {wallet ? `Підключено: ${wallet.slice(0, 6)}...${wallet.slice(-4)}` : 'Підключити гаманець'}
        </button>
      </div>
    </div>
  );
}

export default App;
