import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [wallet, setWallet] = useState(null);
  const [betAmount, setBetAmount] = useState(1);
  const [timer, setTimer] = useState(3600); // 1 година в секундах
  const [bets, setBets] = useState([]);

  useEffect(() => {
    const timerInterval = setInterval(() => {
      // Зменшуємо основний таймер на одну секунду
      if (timer > 0) {
        setTimer((prev) => prev - 1);
      }

      // Оновлюємо значення timeElapsed для кожної ставки на одну секунду
      setBets((prevBets) =>
        prevBets.map((bet) => ({
          ...bet,
          timeElapsed: bet.timeElapsed + 1,
        }))
      );
    }, 1000);

    return () => clearInterval(timerInterval);
  }, [timer]);

  const handleConnectWallet = () => {
    setWallet("0x123456...abcd"); // Замінити на реальне значення після підключення
  };

  const handlePlaceBet = () => {
    if (!wallet) return alert("Підключіть гаманець!");

    const newBet = {
      wallet: wallet,
      amount: betAmount,
      timeElapsed: 0,
    };

    setBets((prevBets) => {
      const updatedBets = [newBet, ...prevBets].slice(0, 9); // Лишаємо 9 останніх ставок
      return updatedBets;
    });

    setBetAmount((prevAmount) => prevAmount + 1); // Збільшуємо ставку
    setTimer(3600); // Рестартуємо таймер
  };

  return (
    <div className="App">
      <h1>Game DApp</h1>
      <div className="button-container">
        <button onClick={handleConnectWallet}>Підключити гаманець</button>
        <button onClick={handlePlaceBet}>Ставка (Ковбой)</button>
      </div>
      <div className="info">
        <p>Ставка: {betAmount} Quai</p>
        {wallet && <p>Гаманець: {wallet}</p>}
        <p>Час до завершення: {Math.floor(timer / 60)}:{timer % 60 < 10 ? "0" : ""}{timer % 60}</p>
      </div>
      <h2>Список ставок</h2>
      <table>
        <thead>
          <tr>
            <th>Гаманець</th>
            <th>Ставка</th>
            <th>Хвилини тому</th>
          </tr>
        </thead>
        <tbody>
          {bets.map((bet, index) => (
            <tr key={index} className={index === 0 ? "winner" : ""}>
              <td>{bet.wallet}</td>
              <td>{bet.amount} Quai</td>
              <td>{Math.floor(bet.timeElapsed / 60)} хв тому</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
