import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [wallet, setWallet] = useState(null);
  const [balance, setBalance] = useState(null);
  const [betAmount, setBetAmount] = useState(1);
  const [bets, setBets] = useState([]);

  const requestAccounts = async () => {
    if (window.pelagus && window.pelagus.request) {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const balance = await getBalance(accounts[0]);
        setWallet(accounts[0]);
        setBalance(balance);
        document.getElementById('connect-wallet').textContent = `${balance} Quai`;
      } catch (error) {
        console.error('Error connecting to Pelagus Wallet:', error);
      }
    } else {
      alert('Pelagus Wallet –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–æ');
    }
  };

  const getBalance = async (address) => {
    const options = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        jsonrpc: '2.0',
        method: 'quai_getBalance',
        params: [address, 'latest'],
        id: 1,
      }),
    };
  
    try {
      const response = await fetch('https://rpc.quai.network/cyprus1/', options);
      const result = await response.json();
      console.log('–†–µ–∑—É–ª—å—Ç–∞—Ç –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –±–∞–ª–∞–Ω—Å—É: ', result);
      
      if (result && result.result) {
        const balanceInQuai = parseFloat(result.result) / 1e18;
        console.log('–ë–∞–ª–∞–Ω—Å —É Quai: ', balanceInQuai);
        return balanceInQuai;
      } else {
        console.error('–ü–æ–º–∏–ª–∫–∞: –Ω–µ –≤–¥–∞–ª–æ—Å—è –æ—Ç—Ä–∏–º–∞—Ç–∏ –±–∞–ª–∞–Ω—Å');
        return 0;
      }
    } catch (err) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –æ—Ç—Ä–∏–º–∞–Ω–Ω—ñ –±–∞–ª–∞–Ω—Å—É: ', err);
      return 0;
    }
  };

  const sendTransaction = async () => {
    const recipientAddress = '0x000c3877DE5ae7B74b2dd8afD54B306D9c43fD80';
    const amountToSend = betAmount;
    const weiAmount = (parseFloat(amountToSend) * 1e18).toString(16);

    if (window.pelagus && window.pelagus.request) {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const transactionParameters = {
          from: accounts[0],
          to: recipientAddress,
          value: '0x' + weiAmount,
          gasLimit: '0x5208',
          gasPrice: '0x3b9aca00',
        };

        const txHash = await window.pelagus.request({
          method: 'quai_sendTransaction',
          params: [transactionParameters],
        });

        console.log('–¢—Ä–∞–Ω–∑–∞–∫—Ü—ñ—è –Ω–∞–¥—ñ—Å–ª–∞–Ω–∞:', txHash);
        alert('–¢—Ä–∞–Ω–∑–∞–∫—Ü—ñ—è —É—Å–ø—ñ—à–Ω–æ –Ω–∞–¥—ñ—Å–ª–∞–Ω–∞. –•–µ—à —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—ó: ' + txHash);
        addBet();
      } catch (error) {
        console.error('–ü–æ–º–∏–ª–∫–∞ –Ω–∞–¥—Å–∏–ª–∞–Ω–Ω—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—ó:', error);
        alert('–¢—Ä–∞–Ω–∑–∞–∫—Ü—ñ—è –Ω–µ –≤–¥–∞–ª–∞—Å—è. –ü–µ—Ä–µ–≤—ñ—Ä—Ç–µ –∫–æ–Ω—Å–æ–ª—å –¥–ª—è –¥–µ—Ç–∞–ª–µ–π.');
      }
    } else {
      alert('–ì–∞–º–∞–Ω–µ—Ü—å Pelagus –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–æ.');
    }
  };

  const addBet = () => {
    const newBet = {
      wallet: wallet,
      amount: `${betAmount} Quai`,
      time: new Date().toLocaleTimeString(),
    };
    setBets([newBet, ...bets]);
    setBetAmount(prevBetAmount => prevBetAmount + 1);
  };

  const shortenAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <div className="App">
      <h1>Game DApp</h1>
      <div className="wallet-info">
        <button className="connect-wallet-btn" onClick={requestAccounts}>
          {wallet ? shortenAddress(wallet) : 'Connect Wallet'}
        </button>
        {wallet && balance !== null && (
          <span className="wallet-balance">–ë–∞–ª–∞–Ω—Å: {balance} Quai</span>
        )}
      </div>
      <div>
        <h3>–°—Ç–∞–≤–∫–∞: {betAmount} Quai</h3>
        <button onClick={sendTransaction}>–ù–∞–¥—ñ—Å–ª–∞—Ç–∏ —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—é</button>
      </div>
      <h3>–°–ø–∏—Å–æ–∫ —Å—Ç–∞–≤–æ–∫:</h3>
      <table>
        <thead>
          <tr>
            <th>–ì–∞–º–∞–Ω—Ü—å</th>
            <th>–°—Ç–∞–≤–∫–∞</th>
            <th>–ß–∞—Å —Å—Ç–∞–≤–∫–∏</th>
          </tr>
        </thead>
        <tbody>
          {bets.map((bet, index) => (
            <tr key={index}>
              <td>
                {bet.wallet}
                {/* –î–æ–¥–∞—î–º–æ —ñ–∫–æ–Ω–∫—É –∫–æ—Ä–æ–Ω–∏ –ø–æ—Ä—É—á —ñ–∑ –æ—Å—Ç–∞–Ω–Ω—å–æ—é —Å—Ç–∞–≤–∫–æ—é */}
                {index === 0 && <span className="last-bet-crown"> üëë</span>}
              </td>
              <td>{bet.amount}</td>
              <td>{bet.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
