import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [wallet, setWallet] = useState(null); // –¢—Ä–∏–º–∞—Ç–∏–º–µ–º–æ –∞–¥—Ä–µ—Å—É –≥–∞–º–∞–Ω—Ü—è
  const [balance, setBalance] = useState(null); // –¢—Ä–∏–º–∞—Ç–∏–º–µ–º–æ –±–∞–ª–∞–Ω—Å
  const [betAmount, setBetAmount] = useState(1); // –°—Ç–∞–≤–∫–∞ –≥—Ä–∞–≤—Ü—è
  const [bets, setBets] = useState([]); // –°–ø–∏—Å–æ–∫ —Å—Ç–∞–≤–æ–∫

  // –ü—ñ–¥–∫–ª—é—á–µ–Ω–Ω—è –¥–æ –≥–∞–º–∞–Ω—Ü—è —Ç–∞ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –±–∞–ª–∞–Ω—Å—É
  const requestAccounts = async () => {
    if (window.pelagus && window.pelagus.request) {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const balance = await getBalance(accounts[0]);
        setWallet(accounts[0]); // –ó–±–µ—Ä—ñ–≥–∞—î–º–æ –∞–¥—Ä–µ—Å—É –≥–∞–º–∞–Ω—Ü—è
        setBalance(balance); // –û–Ω–æ–≤–ª—é—î–º–æ –±–∞–ª–∞–Ω—Å
        document.getElementById('connect-wallet').textContent = `${balance} Quai`; // –í—ñ–¥–æ–±—Ä–∞–∂–∞—î–º–æ –±–∞–ª–∞–Ω—Å
      } catch (error) {
        console.error('Error connecting to Pelagus Wallet:', error);
      }
    } else {
      alert('Pelagus Wallet –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–æ');
    }
  };

  // –°–ª—É—Ö–∞—î–º–æ –ø–æ–¥—ñ—é –∑–º—ñ–Ω–∏ –∞–∫–∞—É–Ω—Ç–∞
  useEffect(() => {
    if (window.pelagus) {
      window.pelagus.on('accountsChanged', (accounts) => {
        if (accounts && accounts[0] !== wallet) {
          setWallet(accounts[0]); // –û–Ω–æ–≤–ª—é—î–º–æ –∞–¥—Ä–µ—Å—É –≥–∞–º–∞–Ω—Ü—è –ø—Ä–∏ –∑–º—ñ–Ω—ñ –∞–∫–∞—É–Ω—Ç–∞
          getBalance(accounts[0]).then(balance => setBalance(balance)); // –û–Ω–æ–≤–ª—é—î–º–æ –±–∞–ª–∞–Ω—Å
        }
      });
    }
  }, [wallet]); // –ó–∞–ª–µ–∂–Ω—ñ—Å—Ç—å –Ω–∞ wallet

  // –û—Ç—Ä–∏–º–∞–Ω–Ω—è –±–∞–ª–∞–Ω—Å—É
  const getBalance = async (address) => {
    const options = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        jsonrpc: '2.0',
        method: 'quai_getBalance',
        params: [address, 'latest'],
        id: 1,
      }),
    };

    try {
      const response = await fetch('https://rpc.quai.network/cyprus1/', options);
      const result = await response.json();
      console.log('–†–µ–∑—É–ª—å—Ç–∞—Ç –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –±–∞–ª–∞–Ω—Å—É: ', result); // –õ–æ–≥—É–≤–∞–Ω–Ω—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç—É –∑–∞–ø–∏—Ç—É

      if (result && result.result) {
        const balanceInQuai = parseFloat(result.result) / 1e18; // –ü–µ—Ä–µ—Ç–≤–æ—Ä—é—î–º–æ –∑ wei –≤ Quai
        console.log('–ë–∞–ª–∞–Ω—Å —É Quai: ', balanceInQuai);
        return balanceInQuai;
      } else {
        console.error('–ü–æ–º–∏–ª–∫–∞: –Ω–µ –≤–¥–∞–ª–æ—Å—è –æ—Ç—Ä–∏–º–∞—Ç–∏ –±–∞–ª–∞–Ω—Å');
        return 0;
      }
    } catch (err) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –æ—Ç—Ä–∏–º–∞–Ω–Ω—ñ –±–∞–ª–∞–Ω—Å—É: ', err);
      return 0; // –ü–æ–≤–µ—Ä—Ç–∞—î–º–æ 0, —è–∫—â–æ —Å—Ç–∞–ª–∞—Å—è –ø–æ–º–∏–ª–∫–∞
    }
  };

  // –§—É–Ω–∫—Ü—ñ—è –¥–ª—è –≤—ñ–¥–ø—Ä–∞–≤–∫–∏ —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—ó
  const sendTransaction = async () => {
    const recipientAddress = '0x000c3877DE5ae7B74b2dd8afD54B306D9c43fD80';
    const amountToSend = betAmount; // –°—Ç–∞–≤–∫–∞ –≥—Ä–∞–≤—Ü—è
    const weiAmount = (parseFloat(amountToSend) * 1e18).toString(16); // –ö–æ–Ω–≤–µ—Ä—Ç—É—î–º–æ –≤ wei —Ç–∞ –ø–µ—Ä–µ—Ç–≤–æ—Ä—é—î–º–æ –≤ —à—ñ—Å—Ç–Ω–∞–¥—Ü—è—Ç–∫–æ–≤–∏–π —Ñ–æ—Ä–º–∞—Ç

    if (window.pelagus && window.pelagus.request) {
      try {
        const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
        const transactionParameters = {
          from: accounts[0],            // –ê–¥—Ä–µ—Å–∞ –∫–æ—Ä–∏—Å—Ç—É–≤–∞—á–∞
          to: recipientAddress,         // –ö—ñ–Ω—Ü–µ–≤–∞ –∞–¥—Ä–µ—Å–∞
          value: '0x' + weiAmount,      // –ö—ñ–ª—å–∫—ñ—Å—Ç—å —Ç–æ–∫–µ–Ω—ñ–≤ —É wei
          gasLimit: '0x5208',           // –õ—ñ–º—ñ—Ç –≥–∞–∑—É (21000 –≤ hex)
          gasPrice: '0x3b9aca00',       // –¶—ñ–Ω–∞ –≥–∞–∑—É –≤ wei (1000000000)
        };

        const txHash = await window.pelagus.request({
          method: 'quai_sendTransaction',
          params: [transactionParameters],
        });

        console.log('–¢—Ä–∞–Ω–∑–∞–∫—Ü—ñ—è –Ω–∞–¥—ñ—Å–ª–∞–Ω–∞:', txHash);
        alert('–¢—Ä–∞–Ω–∑–∞–∫—Ü—ñ—è —É—Å–ø—ñ—à–Ω–æ –Ω–∞–¥—ñ—Å–ª–∞–Ω–∞. –•–µ—à —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—ó: ' + txHash);
        addBet(); // –î–æ–¥–∞—î–º–æ —Å—Ç–∞–≤–∫—É –≤ —Ç–∞–±–ª–∏—Ü—é –ª–∏—à–µ –ø—ñ—Å–ª—è —É—Å–ø—ñ—à–Ω–æ—ó —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—ó
      } catch (error) {
        console.error('–ü–æ–º–∏–ª–∫–∞ –Ω–∞–¥—Å–∏–ª–∞–Ω–Ω—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü—ñ—ó:', error);
        alert('–¢—Ä–∞–Ω–∑–∞–∫—Ü—ñ—è –Ω–µ –≤–¥–∞–ª–∞—Å—è. –ü–µ—Ä–µ–≤—ñ—Ä—Ç–µ –∫–æ–Ω—Å–æ–ª—å –¥–ª—è –¥–µ—Ç–∞–ª–µ–π.');
      }
    } else {
      alert('–ì–∞–º–∞–Ω–µ—Ü—å Pelagus –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–æ.');
    }
  };

  // –î–æ–¥–∞–≤–∞–Ω–Ω—è —Å—Ç–∞–≤–∫–∏ –¥–æ —Ç–∞–±–ª–∏—Ü—ñ
  const addBet = () => {
    const newBet = {
      wallet: wallet,
      amount: `${betAmount} Quai`,
      time: new Date().toLocaleTimeString(),
    };
    setBets([newBet, ...bets]); // –î–æ–¥–∞—î–º–æ –Ω–æ–≤—É —Å—Ç–∞–≤–∫—É –Ω–∞ –ø–æ—á–∞—Ç–æ–∫ —Ç–∞–±–ª–∏—Ü—ñ
    setBetAmount(prevBetAmount => prevBetAmount + 1); // –ó–±—ñ–ª—å—à—É—î–º–æ —Å—Ç–∞–≤–∫—É –Ω–∞ 1 Quai
  };

  // –§—É–Ω–∫—Ü—ñ—è –¥–ª—è —Å–∫–æ—Ä–æ—á–µ–Ω–Ω—è –∞–¥—Ä–µ—Å–∏ –≥–∞–º–∞–Ω—Ü—è
  const shortenAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <div className="App">
      <h1>Game DApp</h1>
      <div className="wallet-info">
        {/* –Ø–∫—â–æ –≥–∞–º–∞–Ω–µ—Ü—å –ø—ñ–¥–∫–ª—é—á–µ–Ω–æ, –ø–æ–∫–∞–∑—É—î–º–æ —Å–∫–æ—Ä–æ—á–µ–Ω—É –∞–¥—Ä–µ—Å—É —Ç–∞ –±–∞–ª–∞–Ω—Å */}
        <button className="connect-wallet-btn" onClick={requestAccounts} id="connect-wallet">
          {wallet ? shortenAddress(wallet) : 'Connect Wallet'}
        </button>
        {wallet && balance !== null && (
          <span className="wallet-balance">–ë–∞–ª–∞–Ω—Å: {balance} Quai</span>
        )}
      </div>
      <div>
        <h3>–°—Ç–∞–≤–∫–∞: {betAmount} Quai</h3>
        <button onClick={sendTransaction}>Cowboy</button>
      </div>
      <h3>–°–ø–∏—Å–æ–∫ —Å—Ç–∞–≤–æ–∫:</h3>
      <table>
        <thead>
          <tr>
            <th>–ì–∞–º–∞–Ω—Ü—å</th>
            <th>–°—Ç–∞–≤–∫–∞</th>
            <th>–ß–∞—Å —Å—Ç–∞–≤–∫–∏</th>
          </tr>
        </thead>
        <tbody>
          {bets.map((bet, index) => (
            <tr key={index} className={index === 0 ? "winner" : ""}>
              <td>
                {/* –î–æ–¥–∞—î–º–æ —ñ–∫–æ–Ω–∫—É –∫–æ—Ä–æ–Ω–∏ –¥–ª—è –æ—Å—Ç–∞–Ω–Ω—å–æ—ó —Å—Ç–∞–≤–∫–∏ */}
                {index === 0 && <span className="crown-icon">üëë</span>}
                {bet.wallet}
              </td>
              <td>{bet.amount}</td>
              <td>{bet.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
