import React, { useState, useEffect } from 'react';
import './App.css';
import WalletConnector from './components/WalletConnector';  // Імпортуємо компонент для роботи з гаманцем

const App = () => {
  const [balance, setBalance] = useState(0);
  const [betAmount, setBetAmount] = useState(1);  // Початкова ставка

  useEffect(() => {
    // Логіка отримання балансу після підключення гаманця
    const fetchBalance = async () => {
      const balanceInQuai = await getBalance();
      setBalance(balanceInQuai);
    };

    fetchBalance();
  }, []);

  // Функція для отримання балансу користувача
  const getBalance = async () => {
    try {
      const accounts = await window.pelagus.request({ method: 'quai_requestAccounts' });
      const account = accounts[0];

      const balanceResponse = await fetch('https://rpc.quai.network/cyprus1/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          jsonrpc: "2.0",
          method: "quai_getBalance",
          params: [account, "latest"],
          id: 1
        })
      });

      const balanceData = await balanceResponse.json();
      const balanceInWei = parseInt(balanceData.result);
      return balanceInWei / 1e18;
    } catch (error) {
      console.error("Error getting balance:", error);
      return 0;
    }
  };

  // Функція для збільшення ставки на 1 Quai
  const increaseBet = () => {
    setBetAmount(betAmount + 1);
  };

  return (
    <div>
      <h1>Game DApp</h1>
      <button id="connect-wallet" onClick={increaseBet}>
        Підключити гаманець: {balance.toFixed(4)} Quai
      </button>
      <div>Поточна ставка: {betAmount} Quai</div>
      <WalletConnector balance={balance} />
    </div>
  );
};

export default App;
